import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { ShieldCheckIcon } from "@heroicons/react/24/outline";

const AdminProfilePage = () => {
  const [admin, setAdmin] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // üîπ Fetch Admin Profile
  useEffect(() => {
    const fetchAdminProfile = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/auth/me", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            // üî∏ ÿÆÿßÿµ ŸäŸÉŸàŸÜ ÿπŸÜÿØŸÉ token ŸÅŸä localStorage
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });

        const data = await res.json();

        if (!data.success) {
          throw new Error(data.message || "Failed to load admin data");
        }

        setAdmin(data.user);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAdminProfile();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-gray-600 font-medium animate-pulse">
          Loading profile...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-red-600 font-medium">{error}</div>
      </div>
    );
  }

  if (!admin) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-gray-600 font-medium">No admin data found</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen pt-16 pb-8 bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-3xl font-bold text-gray-900 mb-8">
            Admin Profile
          </h1>

          <div className="bg-white shadow-md rounded-2xl p-8">
            {/* Header */}
            <div className="flex items-center space-x-6 mb-8">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-600 to-purple-500 rounded-full flex items-center justify-center">
                <span className="text-white font-bold text-2xl">
                  {admin?.name?.charAt(0).toUpperCase()}
                </span>
              </div>
              <div>
                <h2 className="text-2xl font-bold text-gray-900">{admin?.name}</h2>
                <p className="text-gray-600 capitalize flex items-center gap-1">
                  <ShieldCheckIcon className="w-5 h-5 text-blue-500" />
                  {admin?.role}
                </p>
                <p className="text-sm text-gray-500">{admin?.email}</p>
              </div>
            </div>

            {/* Information */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Left Section */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Account Information
                </h3>
                <div className="space-y-3">
                  <div>
                    <label className="text-sm font-medium text-gray-700">
                      Workplace
                    </label>
                    <p className="text-gray-900">{admin?.school}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-700">
                      Admin ID
                    </label>
                    <p className="text-gray-900">{admin?.student_id}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-700">
                      Verification Status
                    </label>
                    <span
                      className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${
                        admin?.is_verified
                          ? "bg-green-100 text-green-800"
                          : "bg-yellow-100 text-yellow-800"
                      }`}
                    >
                      {admin?.is_verified ? "Verified" : "Pending Verification"}
                    </span>
                  </div>
                </div>
              </div>

              {/* Right Section */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Activity Overview
                </h3>
                <div className="space-y-3">
                  <div>
                    <label className="text-sm font-medium text-gray-700">
                      Created At
                    </label>
                    <p className="text-gray-900">
                      {admin?.created_at
                        ? new Date(admin.created_at).toLocaleString()
                        : "N/A"}
                    </p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-700">
                      Last Login
                    </label>
                    <p className="text-gray-900">
                      {admin?.last_login
                        ? new Date(admin.last_login).toLocaleString()
                        : "First time"}
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* Footer */}
            <div className="mt-8 pt-6 border-t border-gray-200">
              <button className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-all duration-200">
                Edit Profile
              </button>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default AdminProfilePage;
